pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

/**
 * @title BaseDocumentStore
 * @notice A base contract for storing and revoking documents
 */
contract BaseDocumentStore is Initializable {
  /**
   * @notice The name of the contract
   */
  string public name;

  /**
   * @notice The version of the contract
   */
  string public version;

  /**
   * @notice A mapping of the document hash to the block number that was issued
   */
  mapping(bytes32 => uint256) public documentIssued;

  /**
   * @notice A mapping of the hash of the claim being revoked to the revocation block number
   */
  mapping(bytes32 => uint256) public documentRevoked;

  /**
   * @notice Emitted when a document is issued
   * @param document The hash of the issued document
   */
  event DocumentIssued(bytes32 indexed document);

  /**
   * @notice Emitted when a document is revoked
   * @param document The hash of the revoked document
   */
  event DocumentRevoked(bytes32 indexed document);

  /**
   * @notice Initialises the contract with a name
   * @param _name The name of the contract
   */
  function __BaseDocumentStore_init(string memory _name) internal onlyInitializing {
    version = "2.3.0";
    name = _name;
  }

  /**
   * @notice Issues a document
   * @param document The hash of the document to issue
   */
  function _issue(bytes32 document) internal onlyNotIssued(document) {
    documentIssued[document] = block.number;
    emit DocumentIssued(document);
  }

  /**
   * @notice Issues documents in bulk
   * @param documents The hashes of the documents to issue
   */
  function _bulkIssue(bytes32[] memory documents) internal {
    for (uint256 i = 0; i < documents.length; i++) {
      _issue(documents[i]);
    }
  }

  /**
   * @notice Gets the block number at which a document was issued
   * @param document The hash of the issued document
   * @return The block number at which the document was issued
   */
  function getIssuedBlock(bytes32 document) public view onlyIssued(document) returns (uint256) {
    return documentIssued[document];
  }

  /**
   * @notice Checks if a document has been issued
   * @param document The hash of the document to check
   * @return A boolean indicating whether the document has been issued
   */
  function isIssued(bytes32 document) public view returns (bool) {
    return (documentIssued[document] != 0);
  }

  /**
   * @notice Checks if a document was issued before a specific block number (inclusive)
   * @param document The hash of the document to check
   * @param blockNumber The block number to check against
   * @return A boolean indicating whether the document was issued before the specified block number
   */
  function isIssuedBefore(bytes32 document, uint256 blockNumber) public view returns (bool) {
    return documentIssued[document] != 0 && documentIssued[document] <= blockNumber;
  }

  /**
   * @notice Revokes a document
   * @param document The hash of the document to revoke
   * @return A boolean indicating whether the document was successfully revoked
   */
  function _revoke(bytes32 document) internal onlyNotRevoked(document) returns (bool) {
    documentRevoked[document] = block.number;
    emit DocumentRevoked(document);

    return true;
  }

  function _bulkRevoke(bytes32[] memory documents) internal {
    for (uint256 i = 0; i < documents.length; i++) {
      _revoke(documents[i]);
    }
  }

  /**
   * @notice Checks if a document has been revoked
   * @param document The hash of the document to check
   * @return A boolean indicating whether the document has been revoked
   */
  function isRevoked(bytes32 document) public view returns (bool) {
    return documentRevoked[document] != 0;
  }

  /**
   * @notice Checks if a document was revoked before a specific block number (inclusive)
   * @param document The hash of the document to check
   * @param blockNumber The block number to check against
   * @return A boolean indicating whether the document was revoked before the specified block number
   */
  function isRevokedBefore(bytes32 document, uint256 blockNumber) public view returns (bool) {
    return documentRevoked[document] <= blockNumber && documentRevoked[document] != 0;
  }

  /**
   * @dev Checks if a document has been issued
   * @param document The hash of the document to check
   */
  modifier onlyIssued(bytes32 document) {
    require(isIssued(document), "Error: Only issued document hashes can be revoked");
    _;
  }

  /**
   * @dev Checks if a document has not been issued
   * @param document The hash of the document to check
   */
  modifier onlyNotIssued(bytes32 document) {
    require(!isIssued(document), "Error: Only hashes that have not been issued can be issued");
    _;
  }

  /**
   * @dev Modifier that checks if a document has not been revoked
   * @param claim The hash of the document to check
   */
  modifier onlyNotRevoked(bytes32 claim) {
    require(!isRevoked(claim), "Error: Hash has been revoked previously");
    _;
  }
}
","keccak256":"0x429b4bbbe47ae4f3eb8aa16a17776ab7e827f71d9b9e4bf713be62bda898f93a","license":"Apache-2.0"},"contracts/DocumentStore.sol":{"content":"// SPDX-License-Identifier: Apache-2.0

pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

import "./BaseDocumentStore.sol";
import "./base/DocumentStoreAccessControl.sol";

/**
 * @title DocumentStore
 * @notice A contract for storing and revoking documents with access control
 */
contract DocumentStore is BaseDocumentStore, DocumentStoreAccessControl {

  address public certAddress;
  string public domainName;

  /**
   * @notice Initialises the contract with a name and owner
   * @param _name The name of the contract
   * @param owner The owner of the contract
   */
  constructor(string memory _name, address owner, string memory _domainName) {
    initialize(_name, owner);
    domainName = _domainName;
  }

  /**
   * @notice Internally initialises the contract with a name and owner
   * @param _name The name of the contract
   * @param owner The owner of the contract
   */
  function initialize(string memory _name, address owner) internal initializer {
    __DocumentStoreAccessControl_init(owner);
    __BaseDocumentStore_init(_name);
  }

  /**
   * @notice Issues a document
   * @param document The hash of the document to issue
   */
  function issue(bytes32 document) public onlyRole(ISSUER_ROLE) onlyNotIssued(document) {
    BaseDocumentStore._issue(document);
  }

  /**
   * @notice Issues multiple documents
   * @param documents The hashes of the documents to issue
   */
  function bulkIssue(bytes32[] memory documents) public onlyRole(ISSUER_ROLE) {
    BaseDocumentStore._bulkIssue(documents);
  }

  /**
   * @notice Revokes a document
   * @param document The hash of the document to revoke
   * @return A boolean indicating whether the revocation was successful
   */
  function revoke(bytes32 document) public onlyRole(REVOKER_ROLE) onlyNotRevoked(document) returns (bool) {
    return BaseDocumentStore._revoke(document);
  }

  /**
   * @notice Revokes documents in bulk
   * @param documents The hashes of the documents to revoke
   */
  function bulkRevoke(bytes32[] memory documents) public onlyRole(REVOKER_ROLE) {
    return BaseDocumentStore._bulkRevoke(documents);
  }

  function setCertAddress(address _certAddress) public onlyRole(DEFAULT_ADMIN_ROLE) {
    certAddress = _certAddress;
  }

  function setDomainName(string memory newDomainName) public onlyRole(DEFAULT_ADMIN_ROLE) {
    domainName = newDomainName;
  }
}
","keccak256":"0xa60e9c89b852801cef7e116183d5c065c5b6a96403d5128ff5d6138d98a8fa3e","license":"Apache-2.0"},"contracts/DocumentStoreCreator.sol":{"content":"// SPDX-License-Identifier: Apache-2.0

pragma solidity ^0.8.0;

import "./DocumentStore.sol";
import "./base/DocumentCertNFT.sol";

/**
 * @title DocumentStoreCreator
 * @dev Naming this factory contract as DocumentStoreCreator so that typechain can name the factory of this
 * contract as DocumentStoreCreatorFactory and it does not collide with the automatically generated
 * DocumentStoreFactory automatically generated by typechain
 */
contract DocumentStoreCreator {

  /**
   * @notice Emitted when a new DocumentStore instance is deployed
   * @param instance The address of the newly deployed DocumentStore instance
   * @param creator The address of the creator of the new DocumentStore instance
   */
  event DocumentStoreDeployed(address indexed instance, address indexed creator);

  /**
   * @notice Emitted when a new DocumentCertNFT instance is deployed
   * @param instance The address of the newly deployed DocumentCertNFT instance
   * @param creator The address of the creator of the new DocumentCertNFT instance
   */
  event DocumentCertNFTDeployed(address indexed instance, address indexed creator);

  /**
   * @notice Deploys a new DocumentStore instance with the given name
   * @param name The name of the new DocumentStore instance
   * @param nftName The name of the new NFT instance
   * @param nftSymbol The symbol of the new NFT instance
   * @param domainName The domain name of the new DocumentStore instance
   * @return The address of the newly deployed DocumentStore instance
   */
  function deploy(
    string memory name,
    string memory nftName,
    string memory nftSymbol,
    string memory domainName
  ) public returns (address, address) {
    // solhint-disable-next-line mark-callable-contracts
    DocumentStore instance = new DocumentStore(name, msg.sender, domainName);
    emit DocumentStoreDeployed(address(instance), msg.sender);

    DocumentCertNFT nft = new DocumentCertNFT(nftName, nftSymbol, address(instance), msg.sender);
    emit DocumentStoreDeployed(address(nft), msg.sender);

    instance.setCertAddress(address(nft));

    return (address(instance), address(nft));
  }
}
","keccak256":"0x7d19181f4b9a64b20811cf457fab282a1b4ec45677a74008d2ffdd5c5bd49edb","license":"Apache-2.0"},"contracts/base/DocumentCertNFT.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Burnable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/Counters.sol";

contract DocumentCertNFT is ERC721, ERC721Enumerable, ERC721URIStorage, ERC721Burnable, AccessControl {
  using Counters for Counters.Counter;
  address public storeAddress;
  bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
  Counters.Counter private _tokenIdCounter;
  mapping(uint256 => string) public merkleHashes;

  constructor(
    string memory name,
    string memory symbol,
    address _storeAddress,
    address owner
  ) ERC721(name, symbol) {

    _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    _grantRole(MINTER_ROLE, msg.sender);
    _grantRole(DEFAULT_ADMIN_ROLE, owner);
    _grantRole(MINTER_ROLE, owner);

    storeAddress = _storeAddress;
    _tokenIdCounter.increment();
  }

  function safeMint(address to, string memory uri, string memory merkleHash) public onlyRole(MINTER_ROLE) {
    uint256 tokenId = _tokenIdCounter.current();
    _tokenIdCounter.increment();
    _safeMint(to, tokenId);
    _setTokenURI(tokenId, uri);
    merkleHashes[tokenId] = merkleHash;
  }

  // The following functions are overrides required by Solidity.
  function _beforeTokenTransfer(address from, address to, uint256 tokenId, uint256 batchSize)
  internal
  override(ERC721, ERC721Enumerable)
  {
    super._beforeTokenTransfer(from, to, tokenId, batchSize);
  }

  function _burn(uint256 tokenId) internal override(ERC721, ERC721URIStorage) {
    super._burn(tokenId);
  }

  function tokenURI(uint256 tokenId)
  public
  view
  override(ERC721, ERC721URIStorage)
  returns (string memory)
  {
    return super.tokenURI(tokenId);
  }

  function supportsInterface(bytes4 interfaceId)
  public
  view
  override(ERC721, ERC721Enumerable, AccessControl, ERC721URIStorage)
  returns (bool)
  {
    return super.supportsInterface(interfaceId);
  }
}
","keccak256":"0x74375c88ce13cdb2a5b1626858434d08d73ed898125a1273b6245994718c3258","license":"MIT"},"contracts/base/DocumentStoreAccessControl.sol":{"content":"// SPDX-License-Identifier: Apache-2.0

pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";

/**
 * @title DocumentStoreAccessControl
 * @notice Base contract for managing access control roles for a DocumentStore
 */
contract DocumentStoreAccessControl is AccessControlUpgradeable {
  bytes32 public constant ISSUER_ROLE = keccak256("ISSUER_ROLE");
  bytes32 public constant REVOKER_ROLE = keccak256("REVOKER_ROLE");

  /**
   * @notice Initialises the contract with the given owner as the default admin, issuer, and revoker
   * @param owner The owner of the contract
   */
  function __DocumentStoreAccessControl_init(address owner) internal onlyInitializing {
    require(owner != address(0), "Owner is zero");
    _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
    _setupRole(DEFAULT_ADMIN_ROLE, owner);
    _setupRole(ISSUER_ROLE, owner);
    _setupRole(REVOKER_ROLE, owner);
  }
}
